mapper.select:
SELECT /* selectSampleBabyList by SampleBabyMapper.xml */
FROM m_sample_baby WHERE 1=1
AND s_item = #{gubun, jdbcType=VARCHAR}
AND s_mname like CONCAT('%',#{searchWord, jdbcType=VARCHAR},'%')
AND s_id like CONCAT('%',#{searchWord, jdbcType=VARCHAR},'%')
AND s_hp like CONCAT('%',#{searchWord, jdbcType=VARCHAR},'%')
AND s_writedate >= #{sStartdate, jdbcType=VARCHAR}
AND s_writedate <= #{sEnddate, jdbcType=VARCHAR}
ORDER BY s_idx DESC

	SELECT COUNT(*)
	FROM m_tvcf_brandbaby
	 WHERE 1=1

       AND c_date >= #{sStartdate, jdbcType=VARCHAR} 


       AND c_date <= #{sEnddate, jdbcType=VARCHAR}


 	   AND c_display = #{cDisplay}


	   AND c_title like CONCAT('%',#{searchWord, jdbcType=VARCHAR},'%')

SELECT c_title, c_img, c_youtube, c_youtube_id, c_date, c_display FROM m_tvcf_brandbaby WHERE 1=1
AND c_date >= #{sStartdate, jdbcType=VARCHAR}
AND c_date <= #{sEnddate, jdbcType=VARCHAR}
AND c_display = #{cDisplay}
AND c_title like CONCAT('%',#{searchWord, jdbcType=VARCHAR},'%')
ORDER BY c_idx DESC LIMIT #{start, jdbcType=INTEGER}, #{length, jdbcType=INTEGER}
