mapper.update:
UPDATE m_beansoup_video
SET
	  m_title = #{mTitle}
	, m_display = #{mDisplay}
	, m_src = #{mSrc}
WHERE
	m_idx = #{mIdx}

UPDATE m_beansoup_news
SET
	  m_title = #{mTitle}
	, m_url = #{mUrl}

	, m_thum = #{mThum}

	, m_display = #{mDisplay}
	, m_ing = #{mIng}
WHERE
	m_idx = #{mIdx}

UPDATE m_beansoup_event
SET
	  m_content = #{mContent}
	, m_product = #{mProduct}
	, m_eday_id = #{mEdayId}  
	, m_type = #{mType}
	, m_display = #{mDisplay}
	, m_ing = #{mIng}											

	, m_thum = #{mThum}
	, m_thum_original = #{mThumOriginal}

WHERE
	m_idx = #{mIdx}

UPDATE 
	m_beansoup_event
SET
	  m_content = #{mContent}
	, m_product = #{mProduct}
	, m_eday_id = #{mEdayId}  
	, m_type = #{mType}
	, m_display = #{mDisplay}
	, m_ing = #{mIng}	
	, m_thum = DEFAULT, m_thum_original =DEFAULT 
