mapper.select:
/* selectWebzineEventList by AdminSaboMapper.xml */ SELECT index_no, cust_name, cust_hp, cust_email, qrt, remark, answer, apply_date FROM m_webzine_event WHERE 1 = 1
AND apply_date >= DATE(#{sStartdate, jdbcType=VARCHAR})
AND apply_date <= DATE(#{sEnddate, jdbcType=VARCHAR})+1
AND cust_name LIKE CONCAT('%', #{searchWord, jdbcType=VARCHAR}, '%')
ORDER BY apply_date DESC LIMIT #{start, jdbcType=INTEGER}, #{length, jdbcType=INTEGER}
