mapper.select:
/* findAdminById by AdminMapper.xml */
SELECT 

  FROM m_member
 WHERE m_id = #{mId}
   AND m_active = 1

mapper.update:
/* updateAdminPwd by AdminMapper.xml */
UPDATE m_member
SET
	m_pwd = #{mPwd}
WHERE
	m_id = #{mId}

mapper.select:
/* checkMember by AdminMapper.xml */
SELECT COUNT(*)
  FROM m_member
 WHERE m_id = #{mId, jdbcType=VARCHAR}
