mapper.update:
UPDATE m_member
SET
	m_pwd = #{mPwd}
	, m_pwd_moddate = NOW()
	, m_auth = #{mAuth}
	, m_name = #{mName}
	, m_year = #{mYear}
	, m_sex = #{mSex}
	, m_hp = #{mHp}
	, m_tel = #{mTel}
	, m_zipcode = #{mZipcode}
	, m_addr1 = #{mAddr1}
	, m_addr2 = #{mAddr2}
	, m_email = #{mEmail}
	, m_smssend= #{mSmssend}
	, m_emailsend = #{mEmailsend}
	, m_ip = #{mIp}
	, m_comp_name = #{mCompName}
	, m_bizno = #{mBizno}
	, m_di = #{mDi}
WHERE
	m_idx = #{mIdx}

mapper.select:
select count(m_id) 
From m_member 
where m_id = #{mId}

mapper.update:
UPDATE m_member
SET
	, m_active 	  = '1' 
	, password 	  = #{password}
WHERE
	m_id = #{mId}

UPDATE m_member
SET
	  m_pwd_moddate = NOW()
	, m_pwd = #{mPwd}
WHERE
	m_idx = #{mIdx}
