AND mbs_active = '1'

SELECT 
	count(*)
FROM 
	m_calendar_model
WHERE 
	c_name = #{cName}
	AND
	c_email = #{cEmail}
	AND
	c_baby_name = #{cBabyName}			
ORDER BY C_IDX DESC
LIMIT 1;
