mapper.insert:
        INSERT INTO m_webzine (
              qrt_year
	        , file_no
	        , title
	        , sns_title
	        , category
	        , remark
	        , insert_date
        ) VALUES (
            #{qrtYear}
            , #{fileNo}
            , #{title}
            , #{snsTitle}
            , #{category}
            , #{remark} 
            , NOW()
        )

        INSERT INTO m_webzine_sendyn (
              m_email
	        , m_insert_date
        ) VALUES (
            #{mEmail}
            , NOW()
        )

        INSERT INTO m_webzine_event (
              cust_name
	        , cust_hp
	        , cust_email
	        , qrt
	        , remark
	        , answer
	        , apply_date
        ) VALUES (
            #{custName}
            , #{custHp}
            , #{custEmail}
            , #{qrt}
            , #{remark}
            , #{answer}
            , NOW()
        )

        INSERT INTO m_sabo (
            s_name
			, s_hp
			, s_memo
			, s_active
			, s_writedate
			, s_email
        ) VALUES (
            #{sName}
            , #{sHp}
            , #{sMemo}
            , #{sActive}
            , NOW()
            , #{sEmail}
        )

mapper.select:
SELECT
    COUNT(*)
FROM
    m_webzine_event
WHERE
    qrt = #{qrt}
 and
 	cust_hp = #{custHp}
 and 
 	cust_email = #{custEmail}
